// Fonction pour soumettre une question
function submitQuestion() {
    const userInput = document.getElementById("userInput").value.toLowerCase();
    const responsesDiv = document.getElementById("responses");

    // Effacer le champ de saisie
    document.getElementById("userInput").value = '';

    // Chercher une réponse dans la FAQ
    const match = faqList.find(faq => userInput.includes(faq.question));

    let responseMessage;
    if (match) {
        responseMessage = match.response;
    } else {
        responseMessage = "Désolé, je ne comprends pas la question,mais je le stoqué pour chercher des informations, Essayez de poser une autre question et merci!";
    }

    // Ajouter les messages à l'interface utilisateur
    responsesDiv.innerHTML += `<div><strong>Vous:</strong> ${userInput}</div>`;
    responsesDiv.innerHTML += `<div><strong>HBA-AI:</strong> ${responseMessage}</div>`;

    // Faire défiler la boîte de chat vers le bas
    const chatbox = document.getElementById("chatbox");
    chatbox.scrollTop = chatbox.scrollHeight;
}


document.getElementById('userInput').addEventListener('keydown', function(event) {
    if (event.key === 'Enter') {
        submitQuestion();
    }
});
